import Todos from './components/Todos';
import './App.css';// in modern Reat apps we can't get red of this import statment here
import Todo from './models/todo';
import NewTodo from './components/NewTodo';
import React, { useState } from 'react';

function App() {
  const [todos,setTodos] = useState<Todo[]>([]);
  const removeTodo = (id:string)=>{
    console.log(id);
    setTodos((previousTodo)=>{
      return previousTodo.filter((item)=>item.id !== id);
    });
  };
  const todoHandler = (item:string)=>{
    const newTodo = new Todo(item)
    setTodos((previousTodo)=>{
      return previousTodo.concat(newTodo);
    });// it is good for making sure that our code match the last snapshot
  };
  return (

    <div className="App">
      <NewTodo onAddtodo={todoHandler}/>
      <Todos items={todos} removeTodo = {removeTodo}/>
    </div>
  );
}

export default App;
